import {ThemeProvider} from "styled-components";
import {lightTheme} from "./Themes";
import styled from 'styled-components';
import {Design, Develope} from './AllSvgs';

const Box = styled.div`
  background: ${props => props.theme.body};
  width: 100vw;
  height: 100vh;
  position: relative;
  display: flex;
  justify-content: space-evenly;
  align-items: center;
`

const Main = styled.div`
  border: 2px solid ${props => props.theme.text};
  color: ${props => props.theme.text};
  background: ${props => props.theme.body};
  padding: 2rem;
  width: 50vw;
  height: 60vh;
  z-index: 3;
  line-height: 1.5;
  font-family: 'Ubuntu Mono',monospace;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
`

const Title = styled.h2`
  display: flex;
  justify-content: center;
  align-items: center;
  font-size: calc(1em + 1vh);
`

const Description = styled.div`
  color: ${props => props.theme.text};
  font-size: calc(.6em + 1vw);
  padding: .5rem 0;
`

const MySkillsPage = () => {
    return (
        <ThemeProvider theme={lightTheme}>
            <Box>
                <Main>
                    <Title>
                        <Develope width={40} height={40} /> Frotend Developer
                    </Title>
{/*                        한번 만들었던 방식은 쉽고 편하지만, 성장에는 한계가 있다고 믿고, 새로운 시도를
                        해보면서 컴포트 존에서 항상 벗어나려고 의식적으로 노력하는 '이승환' 입니다.*/}
                    <h1>Skills</h1>
                    <Description>
                        <h4>HTML / CSS</h4>
                        <ul>
                            <li>시멘틱 마크업을 준수합니다.</li>
                            <li>Search Engine Optimization 경험이 있습니다.</li>
                            <li>Sass 등 CSS Preprocessor 사용할 수 있습니다.</li>
                        </ul>
                        <h4>JavaScript</h4>
                        <p>Html, CSS, JS, React, Redux, Sass, Bootstrap, Firebase etc.</p>
                    </Description>
                </Main>
            </Box>
        </ThemeProvider>
    )
}

export default MySkillsPage;
